import i18n from "i18next";
import LanguageDetector from "i18next-browser-languagedetector";
import { initReactI18next } from "react-i18next";

i18n
  .use(LanguageDetector)
  .use(initReactI18next)
  .init({
    // we init with resources
    resources: {
      en: {
        translations: {
            "SelectSport":"Select a sport",
            "searchteam":"I am looking for a team",
            "Reservation":"Book",
            "searchplayer":"I'm looking for a player",
            "Submit":"Submit",
            "Balls":"Balls and color shirts for each team are included in the prices.",
            "Soccer":"Soccer",
            "SelectDate":"Select an available date",
            "NumberPlayers":"Number of players",
            "Personalinfo":"Personal Information",
            "Firtname":"First Name",
            "Lastnam":"Last Name",
            "PhoneNum":"Phone Number",
            "Book":"Book",
            "Selectlevel":"Select the level",
            "about":"About us",
            "language":"Language",
        }
      },
      fr: {
        translations: {
            "SelectSport":"Selectionnez un sport",
            "searchteam":"Acceuil",
            "exhibition":"Exhibition",
            "about":"À propos",
            "language":"Language",
        }
      }
    },
    fallbackLng: "en",
    debug: true,

    // have a common namespace used around the full app
    ns: ["translations"],
    defaultNS: "translations",

    keySeparator: false, // we use content as keys

    interpolation: {
      escapeValue: false
    }
  });

export default i18n;
